import { useState } from "react";
import { Box } from "@chakra-ui/core";
import { motion } from "framer-motion";
import { css } from "@emotion/core";

const icon = {
  hidden: {
    opacity: 0,
    pathLength: 0,
    fill: "transparent"
  },
  visible: {
    opacity: 1,
    pathLength: 0.9,
    fill: "transparent"
  },
  half: {
    opacity: 0.5,
    pathLength: 0.2,
    fill: "transparent"
  }
};

const Dial = ({ r, s, color }) => {
  return (
    <motion.path
      d={`
            M 100, 100
            m 0, ${-r}
            a ${r},${r} 0 1,0 0,${r * 2}
            a ${r},${r} 0 1,0 0,${-r * 2}
        `}
      strokeWidth={s}
      strokeLinecap="round"
      stroke={color}
      variants={icon}
      initial="hidden"
      animate="visible"
      transition={{
        duration: "5",
        type: "spring",
        stiffness: 40
      }}
    />
  );
};

const Dials = props => {
  const [animate, setAnimate] = useState("visible");

  return (
    <Box w="400px" h="400px" bg="gray.500" p="2">
      <motion.svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 200 200">
        <circle cx="100" cy="25" r="12" fill="white" z="-100" />
        <Dial r={75} s={20} color="red" />
        <Dial r={53} s={20} color="#f2f2f2" />
      </motion.svg>
      <button onClick={() => setAnimate("half")}>test</button>
    </Box>
  );
};

export default Dials;
