import { useState } from "react";
import { Box } from "@chakra-ui/core";
import { motion } from "framer-motion";
import { css } from "@emotion/core";

const Dials = props => {
  const [animate, setAnimate] = useState("visible");

  const icon = {
    hidden: {
      opacity: 0,
      pathLength: 0,
      pathOffset: 0,
      fill: "rgba(255, 255, 255, 0)"
    },
    visible: {
      opacity: 1,
      pathLength: 0.5,
      pathOffset: 0.9,
      fill: "rgba(255, 80, 70, 0)"
    },
    half: {
      opacity: 0.5,
      pathLength: 0.5,
      pathOffset: 0.4,
      fill: "rgba(255, 80, 70, 0.7)"
    }
  };

  return (
    <>
      <Box w="400px" h="400px" bg="gray.500" p="4">
        <motion.svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 216 216"
          css={css`
            overflow: hidden;
            stroke: #fff;
            stroke-width: 5;
            stroke-linejoin: round;
            stroke-linecap: round;
            background: conic-gradient(
              red,
              yellow,
              lime,
              aqua,
              blue,
              magenta,
              red
            );
          `}
        >
          <motion.path
            d="M108 208C52.7715 208 8 163.228 8 108C8 52.7715 52.7715 8 108 8C163.228 8 208 52.7715 208 108C208 163.228 163.228 208 108 208Z"
            variants={icon}
            initial="hidden"
            animate={animate}
            transition={{
              default: { duration: 4, ease: "easeInOut" },
              fill: { duration: 2, ease: [1, 0, 0.8, 1] }
            }}
          />
        </motion.svg>
        <button onClick={() => setAnimate("half")}>test</button>
      </Box>
    </>
  );
};

export default Dials;
