import { useState } from "react";
import { Box } from "@chakra-ui/core";
import { motion } from "framer-motion";
import { css } from "@emotion/core";

const Dials = props => {
  const [animate, setAnimate] = useState("visible");

  const icon = {
    hidden: {
      opacity: 0,
      pathLength: 0,
      fill: "transparent"
    },
    visible: {
      opacity: 1,
      pathLength: 0.8,
      fill: "transparent"
    },
    half: {
      opacity: 0.5,
      pathLength: 0.2,
      fill: "transparent"
    }
  };

  return (
    <Box w="400px" h="400px" bg="gray.500" p="2">
      <motion.svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 200 200">
        <motion.path
          d="
            M 100, 100
            m 0, -75
            a 75,75 0 1,0 0,150
            a 75,75 0 1,0 0,-150
            "
          strokeWidth="20"
          strokeLinecap="round"
          stroke="pink"
          variants={icon}
          initial="hidden"
          animate={animate}
          transition={{
            duration: 1.2,
            type: "spring",
            stiffness: 50
          }}
        />
        <motion.path
          d="
            M 100, 100
            m 0, -50
            a 50,50 0 1,0 0,100
            a 50,50 0 1,0 0,-100
            "
          strokeWidth="20"
          strokeLinecap="round"
          stroke="pink"
          variants={icon}
          initial="hidden"
          animate={animate}
          transition={{
            duration: 1.2,
            type: "spring",
            stiffness: 50
          }}
        />
      </motion.svg>
      <button onClick={() => setAnimate("half")}>test</button>
    </Box>
  );
};

export default Dials;
